import React, { useState } from "react";
import Chart from "../Chart/Chart";

const ExpensesChart = (props) => {
  const chartDataPoint = [
    { label: "Ocak", value: 0 },
    { label: "Şubat", value: 0 },
    { label: "Mart", value: 0 },
    { label: "Nisan", value: 0 },
    { label: "Mayis", value: 0 },
    { label: "Haziran", value: 0 },
    { label: "Temmuz", value: 0 },
    { label: "Agustos", value: 0 },
    { label: "Eylül", value: 0 },
    { label: "Ekim", value: 0 },
    { label: "Kasim", value: 0 },
    { label: "Aralik", value: 0 },
  ];
  for (let expense of props.expenses) {
    const expenseMount = expense.date.getMonth();
    chartDataPoint[expenseMount].value += expense.amount;
  }
  return <Chart dataPoints={chartDataPoint} />;
};
export default ExpensesChart;
